var parser = require('cron-parser');

var options = {
  currentDate: new Date('Wed, 26 Dec 2012 12:38:53 UTC')
};

var interval = parser.parseExpression('* */5 * * * *', options);

console.log(interval.next());
console.log(interval.next());
console.log(interval.next());
console.log(interval.next());
